package uebungenSchleifen;

public class Uebung28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =1;
		
		while (i<10)
		{
			System.out.println(i);
			i++; // laufvariable selber erhöhen
		}
		
		i = 10;
		while (i>1)
		{
			System.out.println(i);
			i--;
		}

	}

}
