
public class doWhileSchleife {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// fussgesteuerte Schleife
		// die Laufbedingung wird am Schleifenfuss geprüft
		// läuft in jedem Falle einmal !!!
		
		int i = 9;
		
		do
		{
			System.out.println(i);
			i++; // bei while und do while 
			     // sind sie verantwortlich für die Bearbeitung der 
			     // Laufvariablen
		} while (i < 10);
		

	}

}
